const data = Inputs.data
const searchEnabled = Inputs.searchString?.length > 0
Outputs.searchEnabled = searchEnabled
const foundedData = Inputs.foundedData

if (data) {
    let filteredData = {}
    if (searchEnabled) {
        filteredData.house = foundedData?.house
        filteredData.area = data.area.filter(a =>
            foundedData?.house?.map(h => h.id).includes(a.house.id)
        )
        filteredData.complex = data.complex.filter(c =>
            foundedData?.house?.map(h => h.complex.id).includes(c.id)
        )
    } else filteredData = data

    filteredData?.house?.map(h => {
        h.agData = {
            counts: { area: filteredData.area.filter(a => a.house.id === h.id).length }
        }
    })

    filteredData?.complex?.map(c => {
        c.agData = {
            counts: {
                house: filteredData?.house?.filter(h => h.complex.id === c.id).length,
                area: filteredData?.house?.filter(h =>
                    h.complex.id === c.id
                ).reduce((acc, obj) => acc + obj.agData.counts.area, 0),
            }
        }
    })

    Outputs.filteredData = filteredData
}

const data = {
    complex: Inputs.complex,
    house: Inputs.house,
    area: Inputs.area,
}
if (
    data.complex
    && data.house
    && data.area    
) Outputs.data = data

