    const newForm = clone(initialForm)
    Script.Outputs.createFormScheme = newForm
    Script.Outputs.setCreateState()
}
Script.Signals.createItem = () => {
    const formHook = Script.Inputs.creeatFormHook
    const houseFormHook = Script.Inputs.houseFormHook

    formHook.validate()

    let areas = houseFormHook.values.area || []
    if (areas.map(a => a.areaName).includes(formHook.values.areaName)) {
        formHook.setErrors({ areaName: 'Вы уже создали зону с таким названием' })
    } else if (formHook.isValid()) {
        areas.push({ ...formHook.values, id: formHook.values.areaName })
        houseFormHook.setValues({ area: areas, areaCount: areas.length })
        Script.Outputs.resetSelected = true
        formHook.reset()
        Script.Outputs.setInitialState()
    }
    Script.Outputs.areas = areas
    Script.Outputs.setTableData()
}
Script.Signals.editForm = () => {
    const editForm = clone(initialForm)
    const selectedItem = Script.Inputs.selectedItem
    editForm[0].initialValue = selectedItem.areaName
    editForm[1].initialValue = selectedItem.areaDescription

    Script.Outputs.editFormScheme = editForm
    Script.Outputs.setEditState()
}
