function dayjsRange(from, to, step, format) {
    const list = []
    let current = Dayjs(from)
    while (current.isBefore(to)) {
        list.push(format ? current.format(format) : current)
        current = current.add(...(typeof step === 'string' ? [1, step] : step))
    }
    return list
}

const dateRangeSelector = Inputs.dateRangeSelector || 'date'
const startDate = Inputs.startDate, endDate = Inputs.endDate

let tableWidth = '100%'

const tasks = Inputs.task
const workers = Inputs.worker

if (tasks?.length && workers?.length) {
    const allTasks = tasks.filter(t =>
        Dayjs(t.content.schedule.startDate.plan)
            .isBetween(startDate, endDate, 'day', '[]') 
    )

    const formatedWorker = workers
        .filter(w =>
            allTasks.map(t => t.worker.id).includes(w.id)
        )
        .map(worker => {
            const tasksOfWorker = allTasks.filter(t => t.worker.id === worker.id)
            const successTasks = tasksOfWorker.filter(t => t.states.flow.value === 'success')
            const failedTasks = tasksOfWorker.filter(t => t.states.flow.value === 'failed')
            const planDurationSum = tasksOfWorker
                .reduce((acc, obj) => acc + obj.content.schedule.duration.plan, 0)
            const planDurationSumFormat = Dayjs.duration(planDurationSum, 'm').format('H[ч] m[м]')
            const factDurationSum = tasksOfWorker
                .reduce((acc, obj) => acc + obj.content.schedule.duration.fact, 0)
            const factDurationSumFormat = factDurationSum
                ? Dayjs.duration(factDurationSum, 'm').format('H[ч] m[м]') : 0
            const diffDuration = planDurationSum - factDurationSum
            let diffFormat = diffDuration
                ? Dayjs.duration(planDurationSum, 'm').format('H[ч] m[м]') : 0
            if (diffDuration < 0) diffFormat = `-${diffFormat}`
            worker.agData = {
                counts: {
                    tasks: tasksOfWorker.length,
                    successTasks: successTasks.length,
                    failedTasks: failedTasks.length
                },
                sums: {
                    planDuration: planDurationSumFormat,
                    factDuration: factDurationSumFormat,
                    diffDuration: diffFormat
                }
            }
            return worker
        })

    Outputs.data = formatedWorker
    Outputs.tableWidth = tableWidth
}

