const data = Inputs.data
const searchEnabled = Inputs.searchString?.length > 0
Outputs.searchEnabled = searchEnabled
const foundedData = Inputs.foundedData
const dateRangeSelector = Inputs.dateRangeSelector || 'date'
let startDate = {}, endDate = {}

if (data) {
    let filteredData = {}
    if (searchEnabled) {
        filteredData.task = data.task.filter(t =>
            foundedData?.house?.map(h => h.id).includes(t.house.id) ||
            foundedData?.complex?.map(c => c.id).includes(t.complex.id)
        )
    } else filteredData = data

    startDate = Dayjs().startOf('day')
    endDate = Dayjs().endOf('day')
    if (dateRangeSelector !== 'period') {
        switch (dateRangeSelector) {
            case 'week': startDate = Dayjs().add(-7, 'd').startOf('day'); break
            case 'month': startDate = Dayjs().add(-30, 'd').startOf('day'); break

        }
    }

    const filteredDataByDateRange = {
        task: filteredData?.task.filter(t =>
            Dayjs(Dayjs(t.content.schedule.startDate.plan))
                .isBetween(startDate, endDate, 'day', '[]')
        )
    }
    filteredDataByDateRange.area = data.area.filter(a =>
        filteredDataByDateRange.task.map(t => t.area.id).includes(a.id)
    )
    filteredDataByDateRange.house = data.house.filter(h =>
        filteredDataByDateRange.task.map(t => t.house.id).includes(h.id)
    )
    filteredDataByDateRange.complex = data.complex.filter(c =>
        filteredDataByDateRange.task.map(t => t.complex.id).includes(c.id)
    )

    filteredDataByDateRange.house.map(h => {
        h.agData = {
            counts: {
                area: filteredDataByDateRange.area.filter(a =>
                    a.house.id === h.id).length,
                plannedTask: filteredDataByDateRange.task.filter(t =>
                    t.house.id === h.id &&
                    t.states.flow.value === 'planned').length,
                inProgressTask: filteredDataByDateRange.task.filter(t =>
                    t.house.id === h.id &&
                    t.states.flow.value === 'inProgress').length,
                successTask: filteredDataByDateRange.task.filter(t =>
                    t.house.id === h.id &&
                    t.states.flow.value === 'success').length,
                failedTask: filteredDataByDateRange.task.filter(t =>
                    t.house.id === h.id &&
                    t.states.flow.value === 'failed').length
            }
        }
    })

    filteredDataByDateRange.house.map(h => {
        h.agData.counts.progress =
            Math.round(
                h.agData.counts.successTask /
                filteredDataByDateRange.task.filter(t => t.house.id === h.id).length
                * 100) + '%'
    })

    filteredDataByDateRange.complex.map(c => {
        c.agData = {
            counts: {
                area: filteredDataByDateRange.house.filter(h =>
                    h.complex.id === c.id
                ).reduce((acc, obj) => acc + obj.agData.counts.area, 0),
                plannedTask: filteredDataByDateRange.house.filter(h =>
                    h.complex.id === c.id
                ).reduce((acc, obj) => acc + obj.agData.counts.plannedTask, 0),
                inProgressTask: filteredDataByDateRange.house.filter(h =>
                    h.complex.id === c.id
                ).reduce((acc, obj) => acc + obj.agData.counts.inProgressTask, 0),
                successTask: filteredDataByDateRange.house.filter(h =>
                    h.complex.id === c.id
                ).reduce((acc, obj) => acc + obj.agData.counts.successTask, 0),
                failedTask: filteredDataByDateRange.house.filter(h =>
                    h.complex.id === c.id
                ).reduce((acc, obj) => acc + obj.agData.counts.failedTask, 0)
            }
        }
    })
    filteredDataByDateRange.complex.map(c => {
        c.agData.counts.progress =
            Math.round(
                c.agData.counts.successTask /
                filteredDataByDateRange.task.filter(t => t.complex.id === c.id).length
                * 100) + '%'
    })

    Outputs.searchEnabled = true
    Outputs.filteredData = filteredDataByDateRange
}

function dayjsRange(from, to, step, format) {
    const list = []
    let current = Dayjs(from)
    while (current.isBefore(to)) {
        list.push(format ? current.format(format) : current)
        current = current.add(...(typeof step === 'string' ? [1, step] : step))
    }
    return list
}

let columns = [
    {
        accessor: '{{content.firstName}} {{content.lastName}}',
        title: 'Исполнитель',
        width: '16rem',
    }
]

const dateRangeSelector = Inputs.dateRangeSelector || 'date'
const startDate = Inputs.startDate, endDate = Inputs.endDate

let tableWidth = '100%'
let rangeObj = {}
switch (dateRangeSelector) {
    case 'date':
        rangeObj = {
            selector: 'day',
            accessor: 'agData.dates.',
            accessorShort: 'dates',
            accessorFormat: 'YYYY-MM-DD',
            titleFormat: 'DD'
        }
        break
    case 'week':
        rangeObj = {
            selector: 'week',
            accessor: 'agData.weeks.',
            accessorShort: 'weeks',
            accessorFormat: 'YYYY_ww',
            titleFormat: 'ww'
        }
        break
    case 'month':
        rangeObj = {
            selector: 'month',
            accessor: 'agData.monthes.',
            accessorShort: 'monthes',
            accessorFormat: 'YYYY-MM',
            titleFormat: 'MM'
        }
        tableWidth = '52rem'
        break
}

let columnsDates = dayjsRange(startDate, endDate, rangeObj.selector).map(d => {
    let isToday = false
    switch (dateRangeSelector) {
        case 'date': isToday = Dayjs(d).dayOfYear() === Dayjs().dayOfYear(); break
        case 'week': isToday = Dayjs(d).week() === Dayjs().week(); break
        case 'month': isToday = Dayjs(d).month() === Dayjs().month(); break
    }
    return {
        accessor: rangeObj.accessor + d.format(rangeObj.accessorFormat),
        title: d.format(rangeObj.titleFormat),
        width: '3rem',
        textAlignment: 'center',
        columnRender: 'avatar',
        titleSx: (theme) => isToday &&
            ({
                '&&': {
                    backgroundColor: theme.colors.dark,
                    color: theme.colors.gray[0]
                }
            }),
        cellsSx: (theme) => isToday &&
            ({
                '&&': {
                    backgroundColor: theme.colors.red[6],
                }
            }),
    }
})
if (dateRangeSelector === 'week') columnsDates = columnsDates.slice(0, columnsDates.length - 1)
columns = columns.concat(columnsDates)

Outputs.columns = columns

const tasks = Inputs.task
const workers = Inputs.worker

if (tasks?.length && workers?.length) {
    const plannedTasks = tasks.filter(t =>
        (t.states.flow.value === 'planned' || t.states.flow.value === 'success') &&
        Dayjs(t.content.schedule.startDate.plan)
            .isBetween(startDate, endDate, 'day', '[]') &&
        Dayjs(t.content.schedule.startDate.plan).isSameOrAfter(Dayjs(), 'date')
    )

    const successTasks = tasks.filter(t =>
        t.states.flow.value === 'success' &&
        Dayjs(t.content.schedule.startDate.fact)
            .isBetween(startDate, endDate, 'day', '[]') &&
        Dayjs(t.content.schedule.startDate.fact).isBefore(Dayjs(), 'date')
    )

    const formatedWorker = workers
        .filter(w =>
            plannedTasks.map(t => t.worker.id).includes(w.id) ||
            successTasks.map(t => t.worker.id).includes(w.id)
        )
        .map(worker => {
            const plannedTasksOfWorker = plannedTasks
                .filter(t => t.worker.id === worker.id)
            const successTasksOfWorker = successTasks
                .filter(t => t.worker.id === worker.id)
            const plannedDates = plannedTasksOfWorker
                .map(t => Dayjs(t.content.schedule.startDate.plan)
                    .startOf(dateRangeSelector).format(rangeObj.accessorFormat))
            const successDates = successTasksOfWorker
                .map(t => Dayjs(t.content.schedule.startDate.fact)
                    .startOf(dateRangeSelector).format(rangeObj.accessorFormat))
            const dupDates = {}
            plannedDates.forEach(function (x) { dupDates[x] = (dupDates[x] || 0) + 1 })
            successDates.forEach(function (x) { dupDates[x] = (dupDates[x] || 0) + 1 })
            worker.agData = { [rangeObj.accessorShort]: dupDates }
            return worker
        })

    Outputs.data = formatedWorker
    Outputs.tableWidth = tableWidth
}

