Script.Signals.filter = () => {
    const tasks = Script.Inputs.tasks
    const selectedStates = Script.Inputs.formHook.values.states
    const selectedComplexes = Script.Inputs.formHook.values.complex
    const selecteDateRange = Script.Inputs.selecteDateRange

    let resultData = {}
    let filtersCount = 0
    if (selectedComplexes?.length) {
        filtersCount++
        resultData = FilterBy.values({
            items: tasks,
            field: 'complex.id',
            values: selectedComplexes,
        })
    }
    if (selectedStates?.length) {
        filtersCount++
        resultData = FilterBy.values({
            items: resultData.r ? resultData.i : tasks,
            field: 'states.flow.value',
            values: selectedStates
        })
    }
    if (selecteDateRange?.filter(i => i).length) {
        filtersCount++
        resultData = FilterBy.dateRange({
            items: resultData.r ? resultData.i : tasks,
            field: 'content.schedule.startDate.plan',
            dateRange: selecteDateRange            
        })
    }

    if (filtersCount === 0) Script.Outputs.tasks = tasks
    else Script.Outputs.tasks = resultData.i

    Script.Outputs.filtersCount = filtersCount
    Script.Outputs.indicatorDisabled = filtersCount === 0
    Script.Outputs.selecteDateRange = selecteDateRange
    Script.Outputs.formScheme = [
        {
            name: 'complex',
            initialValue: selectedComplexes
        },
        {
            name: 'states',
            initialValue: selectedStates
        },
    ]
